import Header from './Header';
import Head from 'next/head';
import { useRouter } from 'next/router';
import fetch from '../pages';

const withLayout = (Page, fetcher) => {

    const pageComponent = (props) => (
        <div>
            <Head>
                <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/4.0.0/css/bootstrap.min.css"
                      integrity="sha384-Gn5384xqQ1aoWXA+058RXPxPg6fy4IWvTNh0E263XmFcJlSAwiGgFAW/dAiS6JXm"
                      crossOrigin="anonymous">
                </link>
            </Head>
            {/*<Header />*/}
            <div className="container">
                <Page {...props} />
            </div>
        </div>
    );

    pageComponent.getInitialProps =  async function() {

        const page = router.query.page ? router.query.page : 1;
        const res = await fetch(`http://api.openmarketcap.com/api/v1/tokens?page=${page}`);

        const data = await res.json();

        console.log(`Show data fetched. Count: ${data.data.length}`);

        return {
            currencies: data
        };
    };;

    return pageComponent;
};



export default withLayout;
